var testAnalyze = function() {

	const tokencontent = "testmeagain";
	const languageIdentifier = new LanguageIdentifier(1, 1);

	/* 8 unique letters with this settings -> 8 NGrams */

	jumlib.assertEquals(8, languageIdentifier.analyze(tokencontent),
			"Text2NGrams 1 test");

	languageIdentifier.setNgramsBounds(1, 4);
	jumlib.assertEquals(41, languageIdentifier.analyze(tokencontent),
			"Text2NGrams 2 test");

	languageIdentifier.setNgramsBounds(3, 5);
	jumlib.assertEquals(30, languageIdentifier.analyze(tokencontent),
			"Text2NGrams 3 test");
}

var testIdentify = function() {

	const languageIdentifier = new LanguageIdentifier();

	for (item in testArray) {
		var text = testArray[item].text;
		var language = testArray[item].language;

		jumlib.assertEquals(language, languageIdentifier.identify(text),
				"Test for '" + language + "' language");
	}
}

